import React from "react";
import { differenceInDays, formatDistanceToNow } from 'date-fns';
import { tr } from "date-fns/locale";

const Task = ({ taskObj, onComplete }) => {
  /*
  const d = new Date();
  const deadlineDate = typeof taskObj.deadline === 'string'
  ? parseISO(taskObj.deadline)
  : taskObj.deadline;
  const result = differenceInDays(deadlineDate,d)
  */

  const deadline = new Date(taskObj.deadline);

  const distance = formatDistanceToNow(deadline, {
    locale: tr,
    addSuffix: true,
  });

  const today = new Date();

  function getClassName(date) {
    const difference = differenceInDays(date, today);

    return difference > 3 ? "normal" : "urgent";
  }
  const bg = getClassName(deadline);


  return (
    <div className="task">
      <h3>{taskObj.title}</h3>
      <div className="deadline">son teslim: <span>{bg}</span></div>
      <p>{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>{p}</span>
        ))}
      </div>
      {onComplete && <button onClick={() => onComplete(taskObj.id)}>TamamlandÄ±</button>}
    </div>
  );
};

export default Task;
