import { differenceInDays, formatDistanceToNow } from 'date-fns';
import { tr } from "date-fns/locale";

const Task = ({ taskObj, onComplete }) => {
  /*
  const d = new Date();
  const deadlineDate = typeof taskObj.deadline === 'string'
  ? parseISO(taskObj.deadline)
  : taskObj.deadline;
  const result = differenceInDays(deadlineDate,d)
  */

  const isLeft = formatDistanceToNow(new Date(taskObj.deadline), {
    locale: tr,
  });

  const timeLeft = differenceInDays(new Date(taskObj.deadline), new Date());



  return (
    <div className="task">
      <h3>{taskObj.title}</h3>
      <div className="deadline">son teslim: <span>{timeLeft}</span></div>
      <p>{taskObj.description}</p>
      <div>
        {taskObj.people.map((p) => (
          <span className="pill" key={p}>{p}</span>
        ))}
      </div>
      {onComplete && <button onClick={() => onComplete(taskObj.id)}>TamamlandÄ±</button>}
    </div>
  );
};

export default Task;
